import ComposableArchitecture
import SwiftUI

struct HouseListView: View {
    let store: Store<HouseListState, HouseListAction>

    var body: some View {
        WithViewStore(
            store.scope(
                state: { $0.view },
                action: { (viewAction: HouseListView.Action) in
                    viewAction.feature
                }
            )
        ) { viewStore in
            if viewStore.isInitialLoading {
                Color.clear
                    .frame(height: 0)
                    .onAppear {
                        viewStore.send(.onAppear)
                    }
            }

            if viewStore.isLoading {
                ProgressView()
            } else {
                // TODO: selection (selection: $selectedHouse (HouseMetadataModel))
                List {
                    ForEachStore(
                        store.scope(
                            state: \.rowStates,
                            action: HouseListAction.row
                        )
                    ) { rowStore in
                        HouseListRowView(store: rowStore)
                    }
                }
                .alert(
                    self.store.scope(state: { $0.alertState }),
                    dismiss: .alertDismissed
                )
                .refreshable {
                    await viewStore.send(.refresh, while: \.isLoading)
                }
                .navigationTitle("Houses")
            }
        }
    }
}
